import React from 'react';

import Highcharts, { numberFormat } from 'highcharts/highcharts';
import HighchartsReact from 'highcharts-react-official';
import ColumnGraph from './ColumnGraph';



 const nf = new Intl.NumberFormat();

class WorldPieChart extends React.Component{

  constructor(props)
  {
    super();
    this.state={options:{},data:[]};
  }

  componentDidMount()
  {
     this.structureData();
  }


  structureData()
  {
    let data = {};
    try{
    data["TotalConfirmed"] = this.props.data.total.confirmed[this.props.data.total.confirmed.length-1][1];
    data["TotalDeaths"] = this.props.data.total.deaths[this.props.data.total.deaths.length-1][1];
    data["TotalRecovered"] = this.props.data.total.recovered[this.props.data.total.recovered.length-1][1];

    data["NewConfirmed"] = data["TotalConfirmed"]-this.props.data.total.confirmed[this.props.data.total.confirmed.length-2][1];
    data["NewRecovered"] = data["TotalRecovered"]-this.props.data.total.recovered[this.props.data.total.recovered.length-2][1];
    data["NewDeaths"] = data["TotalDeaths"]-this.props.data.total.deaths[this.props.data.total.deaths.length-2][1];
     

    let  activecases = data.TotalConfirmed-(data.TotalDeaths+data.TotalRecovered);
    let  obj =[{"name": "Active","data":activecases,"y": parseFloat(((activecases/data.TotalConfirmed)*100).toFixed(2))},{"name":"Deaths","data":data.TotalDeaths,"y":parseFloat(((data.TotalDeaths/data.TotalConfirmed)*100).toFixed(2))},{"name":"Recovered","data":data.TotalRecovered,"y":parseFloat(((data.TotalRecovered/data.TotalConfirmed)*100).toFixed(2))}];
    this.setState({
      options:{
        chart: {
            type: 'pie',
            height:'320',
          },
          title: {
            text: this.props.presentRegion +' Coronovirus Cases'
          },    
          tooltip: {
            formatter: function() {
              return this.point.name+":<b>"+this.point.y+"%</b>"
            },
          },
          accessibility: {
            point: {
              valueSuffix: '%'
            }
          },
          plotOptions: {
            pie: {
              allowPointSelect: true,
              cursor: 'pointer',
              dataLabels: {
                enabled: false,
                format: '{point.y}%'
              },
              showInLegend: true
            }
          },
          credits: {
            enabled: false
        },
          series: [{
            name: 'Cases',
            colorByPoint: true,
            data: obj,
          }]
      },
      data:[
        nf.format(data.TotalConfirmed),
        nf.format(data.NewConfirmed),
        nf.format(data.TotalRecovered),
        nf.format(data.NewRecovered),
        nf.format(data.TotalDeaths),
        nf.format(data.NewDeaths)
      ]
    });

   }
   catch(err){
     console.log(err);
   }

      

  }


    render() {
        return(     
        <div>
          <div className="container-fluid">
          <div className="row">
            <div className="col-xl-2 col-md-6">
               <div className="card casescountcard" style={{borderLeft:'4px solid rgb(124, 181, 236)'}}>
                  <div className='card-header'>
                     <i className="fa fa-users" style={{fontSize:'25px',color:"rgb(124, 181, 236)"}}></i><br></br>
                     <span style={{textAlign:"center",fontSize:'24px',fontWeight:'500'}}>{this.state.data[0]}</span>   
                  </div>
                  <div className="card-body" style={{marginTop:"-24px"}}><span>Total Confirmed</span></div>
               </div>  
            </div>
            <div className="col-xl-2 col-md-6 ">
              <div className="card casescountcard" style={{borderLeft:'4px solid rgb(124, 181, 236)'}}>
                  <div className='card-header'>
                  <i className="fa fa-users" style={{fontSize:'25px',color:"rgb(124, 181, 236)"}}></i><br></br>
        <span style={{textAlign:"center",fontSize:'24px',fontWeight:'500'}}>{this.state.data[1]}</span>
                  </div>  
                  <div className="card-body" style={{marginTop:"-24px"}}>New Confirmed</div>
               </div> 
            </div>
             <div className="col-xl-2 col-md-6">
               <div className="card casescountcard" style={{borderLeft:'4px solid #7aed7a'}}>
                  <div className='card-header'>
                    <i className="fa case-icon fa-inverse fa-child" style={{fontSize:'25px',color:"#7aed7a",fontWeight:"bold"}}></i><br></br>
                    <span style={{textAlign:"center",fontSize:'24px',fontWeight:'500'}}>{this.state.data[2]}</span>
                  </div>
                  <div className="card-body" style={{marginTop:"-24px"}}>Total Recovered</div>
               </div>  
              </div>
             <div className="col-xl-2 col-md-6">
              <div className="card casescountcard" style={{borderLeft:'4px solid #7aed7a'}}>
                  <div className='card-header'>
                   <i className="fa case-icon fa-inverse fa-child" style={{fontSize:'25px',color:"#7aed7a",fontWeight:"bold"}}></i><br></br>
        <span style={{textAlign:"center",fontSize:'24px',fontWeight:'500'}}>{this.state.data[3]}</span>        
                  </div>
                  <div className="card-body" style={{marginTop:"-24px"}}>New Recovered</div>
               </div> 
              </div>
            
            <div className="col-xl-2 col-md-6">
               <div className="card casescountcard" style={{borderLeft:'4px solid #ef3c3c'}}>
                  <div className='card-header'>
                    <i className="fas fa-heart-broken" style={{fontSize:'25px',color:"#ef3c3c"}}></i><br></br>
                    <span style={{textAlign:"center",fontSize:'23px',fontWeight:'500'}}>{this.state.data[4]}</span>
                  </div>
                  <div className="card-body" style={{marginTop:"-24px"}}>Total Deaths</div>
               </div> 
            </div> 
            <div className="col-xl-2 col-md-6">
              <div className="card casescountcard" style={{borderLeft:'4px solid #ef3c3c'}}>
                 <div className='card-header'>
                    <i className="fas fa-heart-broken" style={{fontSize:'25px',color:"#ef3c3c"}}></i><br></br> 
                    <span style={{textAlign:"center",fontSize:'23px',fontWeight:'500'}}>{this.state.data[5]}</span>
                 </div>   
                  <div className="card-body" style={{marginTop:"-24px"}}>New Deaths</div>
               </div> 
            </div>    
          </div>
          

          <div className="row">
            <div className="col-xl-4 col-md-5">
              <div className="card ">
                 <div className="card-body">
                   <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                 </div>
              </div>
            </div>

            <div className="col-xl-8 col-md-7">
               <ColumnGraph  presentRegion={this.props.presentRegion} data={this.props.data}/>
            </div>
          </div>
          </div>
        </div>
        );
    }
}

export default WorldPieChart;
