import React from 'react';
import {ProgressBar} from 'primereact/progressbar';


class AffectedCountries extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
           data:[],
           name:'',
        }
        
    }

    componentDidMount()
    {
      this.sortData();       
    }

    sortData()
    {
        let data = this.props.data,name='';
        if(this.props.presentRegion === 'World'){
          data = data.Countries;
          name="Countries";
          data.sort((a,b) => {return b.TotalConfirmed-a.TotalConfirmed})
          data.forEach(element => {
             element["percentage"] = (element.TotalConfirmed/this.props.data.Global.TotalConfirmed)*100;
             element["place"] = element.Country;
           });
        }
        else{
          name="States";
          data.sort((a,b) => {return b.noOfCases-a.noOfCases})  
          for(let i =0,j=data.length;i<j;i++)
          {
            if(i>4)  break;
            data["place"]=data[i].state;
            data["percentage"] = data[i].noOfCases/this.props.TotalConfirmed
          }  
        }

        this.setState({data: data,name:name});
  
        
      }

    render()
    {
        return(
           <div className="col-xl-3 col-md-4">
              <div className="card" style={{height:'360px'}}>
                <div className="card-header">5 Most Affected {this.state.name}</div>
                <div className="card-body">
                  { this.state.data.map(name => ( 
                     <div key={name.country}>
                       <span  style={{float:'left'}}>{name.place}</span>  <span style={{float:"right"}}>{name.TotalConfirmed.toLocaleString()}</span><br></br>
                       <ProgressBar value={name.percentage} showValue ={false} style={{height:'5px'}} />
                       <br></br>
                     </div>   ))}
                </div>
              </div>
            </div>
        );
    }
}

export default AffectedCountries;
