                },
                yAxis: {
                    min: 0,
                    title: {
                        text: 'Cases'
                    }
                },
                series: [{
                    name:'Cases',
                    data: obj
            
                }]
            }
        })
    }

    render()
    {
        return(
            <div className="card">
                <div className="card-body">
                  <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                </div>
            </div>
        )
    }
}

