import  React  from "react";


class Rates extends React.Component{
    constructor(props)
    {
        super();
        this.state={active:{},recovered:{},deaths:{}}

    }

    componentDidMount(){
        this.structureData();
    }

   async structureData(){
         let data=[];

        //find the highest active cases rate
        Object.keys(this.props.data.data).forEach((x,index)=>{
            if(index==0){
                data.push(
                {name:x,percent:((this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1]-this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]-this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1])/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100},
                {name:x,percent:(this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100},
                {name:x,percent:(this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100}
                )
            }
            else{
                if(data[0].percent< ((this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1]-this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]-this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1])/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100){
                    data[0].name=x;
                    data[0].percent=((this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1]-this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]-this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1])/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100
                }
                if(data[1].percent< (this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100){
                    data[1].name=x;
                    data[1].percent=(this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100
                }
                if(data[2].percent< (this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100){
                    data[2].name=x;
                    data[2].percent=(this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100
                }
            }
        });
        data[0].percent=data[0].percent.toFixed(2);
        data[1].percent=data[1].percent.toFixed(2);
        data[2].percent=data[2].percent.toFixed(2);
        await this.setState({active:data[0],recovered:data[1],deaths:data[2]});
        console.log(this.state.active.name);

    }


    render(){
        const {active,recovered,deaths} = this.state;  
        return(
            <div className="col-xl-3 col-md-12">
              <div className="card" style={{height:"360px"}}>
                <div className="card-body">
                    {/* <span style={{float:"left"}}>Highest Active Rate</span>
                    <hr style={{border:"2px solid rgb(124, 181, 236)"}}></hr>
                    <span style={{float:"left"}}>{active.name}</span>
                    <span style={{float:"right",color:"#ef3c3c"}}>{active.percent}%</span><br></br><br></br>
                    <hr></hr>
                    <span style={{float:"left"}}>Highest Recovery Rate</span>
                    <hr style={{border:"2px solid #009933"}}></hr>
                    <span style={{float:"left"}}>{recovered.name}</span>
                    <span style={{float:"right",color:"#009933"}}>{recovered.percent}%</span><br></br><br></br><br></br>
                    <hr></hr>
                    <span style={{float:"left"}}>Highest Death Rate</span>
                    <hr style={{border:"2px solid red"}}></hr>
                    <span style={{float:"left"}}>{deaths.name}</span>
                    <span style={{float:"right",color:"red"}}>{deaths.percent}%</span> */}
                    <div>
                      <h6>Highest Active Rate</h6><hr style={{borderColor:"blue",border:"1px solid blue"}}></hr>
                      <span style={{float:"left"}}>{active.name}</span>
                    </div><br></br><br></br>
                    <div>
                    <h6>Highest Active Rate</h6><hr style={{borderColor:"green"}}></hr>
                      <span style={{float:"left"}}>{active.name}</span>
                    </div>
                    <br></br><br></br>
                    <div>
                    <h6>Highest Active Rate</h6><hr style={{borderColor:"red"}}></hr>
                      <span style={{float:"left"}}>{active.name}</span>
                    </div>

                </div>
              </div>
            </div>
        )
    }
}

export default Rates;
