import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import highchartsMap from "highcharts/modules/map";
import world from "@highcharts/map-collection/custom/world.geo.json";
import india from "@highcharts/map-collection/countries/in/custom/in-all-disputed.geo.json";
import code from "./../countrycordinates.json";

highchartsMap(Highcharts);


var data=[];
class CoronoMap extends React.Component{

  constructor(props)
  {
    super();
    this.state={mapOptions:{},mapdata:props.presentRegion === "World" ? world:india};
  }

  componentDidMount(){
    this.structureData();
    let options={};
    if(this.props.presentRegion === 'India')
    options = {
      chart: {
        map:this.state.mapdata,
        height:320
      },
      title: {
        text: this.props.presentRegion+" Map Plot with Cases"
      },
      
      credits:{
        enabled:false
      },
      mapNavigation: {
        enabled: true,
        buttonOptions: {
          verticalAlign: "bottom"
        }
      },
      tooltip: {
        headerFormat: '{point.name}<br>',
        pointFormat: '{point.y} m a. s. l.',
        shared: true
    },
      series: [
        {
          data: data,
          name: this.props.presentRegion === 'World'?'Countries':'States',
          states: {
            hover: {
              color: "red"
            }
          }
        }
      ]
    }
    else
       options = {
         chart: {
        map:this.state.mapdata,
        height:320
      },
      title: {
        text: this.props.presentRegion+" Map Plot with Cases"
      },
      
      credits:{
        enabled:false
      },
      mapNavigation: {
        enabled: false,
        buttonOptions: {
          verticalAlign: "bottom"
        }
      },
      
      series: [
        {
          data: this.props.presentRegion === 'World'?code.ref_country_codes:data,
          joinBy: ["iso-a3", "alpha3"],
          name: this.props.presentRegion === 'World'?'Countries':'States',
          states: {
            hover: {
              color: "red"
            }
          }
        }
      ]};
    this.setState({
      mapOptions: options
  });
}

  structureData()
  {
    if(this.props.presentRegion === 'India'){
      this.props.data.countries.forEach(country=>{
        data.push([country === "Arunachal Pradesh"?"arunanchal pradesh":country.toLowerCase(),parseInt(this.props.data.data[country].confirmed[this.props.data.data[country].confirmed.length-1][1]).toLocaleString()])
      })
      return;
    }
    this.props.data.countries.forEach(country=>{
      code.ref_country_codes.forEach(x=>{
         if(x.country.toLocaleLowerCase() === country.toLowerCase()) x["value"] = parseInt(this.props.data.data[country].confirmed[this.props.data.data[country].confirmed.length-1][1]).toLocaleString();
      })
    })
  }

    render(){
        return (
          <div className="col-xl-9 col-md-8">
              <div className="card" style={{marginBottom:'30px'}}>
                 <div className="card-body">
                    <HighchartsReact constructorType ={'mapChart'} highcharts={Highcharts} options={this.state.mapOptions} />
                 </div>
              </div>
          </div>
        );
    }
}


export default CoronoMap;
