import React from 'react';

import Highcharts, { numberFormat } from 'highcharts/highcharts';
import HighchartsReact from 'highcharts-react-official';
import ColumnGraph from './ColumnGraph';



 const nf = new Intl.NumberFormat();

class WorldPieChart extends React.Component{

  constructor(props)
  {
    super();
    this.state={options:{},data:[],url:props.url};
  }

  
  pieData=[];

  componentWillReceiveProps(props) {
    if(!props.refresh)
      return;
    else {
       this.getData(props);
    }
  }

  componentDidMount()
  {
    this.getData(this.props);
  }


  // GET DATA FOR THE CORRESPONDING REGION OR GLOBAL
  getData(value)
  { 
    fetch(value.url)
    .then(response => response.json())
    .then(data => {        
       this.structureData(data); 
     });
  }


  structureData(data)
  {
     var activecases = data.cases-(data.deaths+data.recovered);
     var obj =[{"name": "Active","data":activecases,"y": parseFloat(((activecases/data.cases)*100).toFixed(2))},{"name":"Deaths","data":data.deaths,"y":parseFloat(((data.deaths/data.cases)*100).toFixed(2))},{"name":"Recovered","data":data.recovered,"y":parseFloat(((data.recovered/data.cases)*100).toFixed(2))}];
     this.setState({
      options:{
        chart: {
            type: 'pie',
            height:'320',
          },
          title: {
            text: this.props.presentRegion +' Cornovirus Cases'
          },    
          tooltip: {
            pointFormat: '{series.name}: <b>{point.percentage:.1f} % </b>'
          },
          accessibility: {
            point: {
              valueSuffix: '%'
            }
          },
          plotOptions: {
            pie: {
              allowPointSelect: true,
              cursor: 'pointer',
              dataLabels: {
                enabled: false
              },
              showInLegend: true
            }
          },
          credits: {
            enabled: false
        },
          series: [{
            name: 'Cases',
            colorByPoint: true,
            data: obj,
          }]
      },
      data:[
        nf.format(data.cases),
        nf.format(data.todayCases),
        nf.format(data.recovered),
        nf.format(data.deaths),
        nf.format(data.todayDeaths)
      ]
    });
      

  }


    render() {
        return(     
        <div>
          <div className="container-fluid">
          <div className="row">
            <div className="col-xl-2 col-md-6">
               <div className="card casescountcard" style={{borderLeft:'4px solid rgb(124, 181, 236)'}}>
                  <div className='card-header'>
        <span style={{textAlign:"center",fontSize:'23px'}}>{this.state.data[0]}</span><span className="fa fa-circle" style={{color:"rgb(124, 181, 236)",fontSize:'33px',float:'right'}}></span><br></br>      
                  </div>
                  <div className="card-body">Total Confirmed</div>
               </div>  
            </div>
            <div className="col-xl-2 col-md-6 ">
              <div className="card casescountcard" style={{borderLeft:'4px solid rgb(124, 181, 236)'}}>
                  <div className='card-header'>
        <span style={{textAlign:"center",fontSize:'23px'}}>{this.state.data[1]}</span><span className="fa fa-circle" style={{color:"rgb(124, 181, 236)",fontSize:'33px',float:'right'}}></span>
                  </div>  
                  <div className="card-body">New Confirmed</div>
               </div> 
            </div>
             <div className="col-xl-2 col-md-6">
               <div className="card casescountcard" style={{borderLeft:'4px solid #7aed7a'}}>
                  <div className='card-header'>
        <span style={{textAlign:"center",fontSize:'23px'}}>{this.state.data[2]}</span><span className="fa fa-circle" style={{color:"#7aed7a",fontSize:'33px',float:'right'}}></span>
                  </div>
                  <div className="card-body">Total Recovered</div>
               </div>  
              </div>
             <div className="col-xl-2 col-md-6">
              <div className="card casescountcard" style={{borderLeft:'4px solid #7aed7a'}}>
                  <div className='card-header'>
                    <span style={{textAlign:"center",fontSize:'23px'}}>5,32,6347</span><span className="fa fa-circle" style={{color:"#7aed7a",fontSize:'33px',float:'right'}}></span>                    
                  </div>
                  <div className="card-body">New Recovered</div>
               </div> 
              </div>
            
            <div className="col-xl-2 col-md-6">
               <div className="card casescountcard" style={{borderLeft:'4px solid #ef3c3c'}}>
                  <div className='card-header'>
        <span style={{textAlign:"center",fontSize:'23px'}}>{this.state.data[3]}</span><span className="fa fa-circle" style={{color:"#ef3c3c",fontSize:'33px',float:'right'}}></span>
                  </div>
                  <div className="card-body">Total Deaths</div>
               </div> 
            </div> 
            <div className="col-xl-2 col-md-6">
              <div className="card casescountcard" style={{borderLeft:'4px solid #ef3c3c'}}>
                 <div className='card-header'>
        <span style={{textAlign:"center",fontSize:'23px'}}>{this.state.data[4]}</span><span className="fa fa-circle" style={{color:"#ef3c3c",fontSize:'33px',float:'right'}}></span>
                 </div>   
                  <div className="card-body">New Deaths</div>
               </div> 
            </div>    
          </div>
          


          <div className="row">
            <div className="col-xl-4 col-md-8">
              <div className="card pie">
                 <div className="card-body">
                   <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                 </div>
              </div>
            </div>

            <div className="col-xl-8 col-md-8">
               <ColumnGraph  presentRegion={this.props.presentRegion}/>
            </div>
          </div>
          </div>
        </div>
        );
    }
}

export default WorldPieChart;
