import React from 'react';
// import TopPanel from './components/TopPanel';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import WorldPieChart from './components/WorldPieChart';
import CoronoMap from './components/CoronaMap';
import CountryData from './components/tableData';
import AffectedCountries from './components/AffectedCountries';
import {BallBeat} from 'react-pure-loaders';


const API = "https://coronavirus-19-api.herokuapp.com/countries";

//const AffectedPlacesAPI = '';


class App extends React.Component {

  constructor() {
    super();
    this.state = {presentRegion:'World',loading:true};
    this.showSelectedCountryData =this.showSelectedCountryData.bind(this);
    this.showGlobalData = this.showGlobalData.bind(this);
  }


  componentDidMount() {
    this.getData();
  }

   getData()
  {
    this.getPieAndNumbersData();
    this.getColumnGraphData();
    this.getAffectedPlacesAndTableData();
  }

  async getPieAndNumbersData()
  {
    try{
      const fetcher = await fetch(this.state.presentRegion === 'World' ? API+"/World" : API+"/India")
      const response = await fetcher.json();
      this.setState({pieData:response});
    }
    catch(err)
    {
      console.log(err);
    }
  }

 async getColumnGraphData()
 {
   try{
   const fetcher =  await fetch('https://api.covid19api.com/dayone/country/India/status/confirmed')
   const response = await fetcher.json()
   this.setState({columnData:response})
   }
   catch(err)
   {
      console.log(err);
   }
  }

  async getAffectedPlacesAndTableData(){
    try{
    const fetcher = await fetch(this.state.presentRegion === 'World' ? "https://coronavirus-19-api.herokuapp.com/countries" : "https://covid-india-cases.herokuapp.com/states/");
    const response = await fetcher.json();
    console.log(response,"resooo");
    if(this.state.pieData == undefined || this.state.columnData == undefined) {
      setTimeout(() => { 
        this.setState({
         placesData:response,
         loading:false
        }); 
    }, 2000);}
    else
      this.setState({
        placesData:response,
        loading:false
      });  
  }
  catch(err){
    console.log(err);
  }


  }

  async showSelectedCountryData() {
    await this.setState({
      presentRegion:'India',
      loading:true
    });
    this.getData();
  }

  async showGlobalData(){ 
    await this.setState({presentRegion:'World',loading:true});
    this.getData();
  }

  render() {
    const {region,loading} = this.state;
    let pageData,loader;
    if(!loading){
      pageData = <div>
    <div className="header">
      <h5 style={{ padding: '10px' }}>
        <a href="/" className="navbar-brand router-link-exact-active active" target="_self"><img src="https://covid19site.netlify.app/img/virus.3d9b68c5.png" alt="Covid" height="30" width="30" className="d-inline-block align-top mr-1" />COVID-19 </a>
        <div style={{ float: 'right' }}>
          <span className="locations" onClick={this.showGlobalData}><i className="fa fa-globe" style={{ color: '#4f96ae' }}></i>&nbsp;World</span>
          <span style={{ marginLeft: '25px' }} className="locations" onClick={this.showSelectedCountryData}><i className="fa fa-map-marker" style={{ color: 'red' }}></i>&nbsp;India</span>
        </div>
      </h5>
    </div>
    <WorldPieChart presentRegion={this.state.presentRegion} piedata={this.state.pieData} columndata={this.state.columnData}/>
    <div className="container-fluid">
      <div className="row" >
        <AffectedCountries  data={this.state.placesData} presentRegion={this.state.presentRegion} />
        <CoronoMap />
      </div>
    </div>
    <CountryData  presentRegion={this.state.presentRegion} data={this.state.placesData} /><br></br><br></br>
    </div>
  }
  else{
    loader = 
    <div className = "fetching">
      <img src="https://covid19site.netlify.app/img/virus.3d9b68c5.png" alt="image" height="50px"></img>
      <BallBeat 
        color={'#123abc'}
        loading={this.state.loading}
      />
    </div>
  }

    return (
      <div style={{ backgroundColor: "rgb(248,248,248)" }}>
        {loader}
       {pageData}
      </div>
    );
  }
}

export default App;
