import React from 'react';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import WorldPieChart from './components/WorldPieChart';
import CoronoMap from './components/CoronaMap';
import CountryData from './components/tableData';
import AffectedCountries from './components/AffectedCountries';
import Check from './components/Rates';


const WorldDataApi = "https://nprelease.indiatimes.com/ncov19/trend_data_toi.json";
const IndiaDataApi = "https://nprssfeeds.indiatimes.com/ncov19/trend_data_india_toi.json";

class App extends React.Component {

  constructor() {
    super();
    this.chartData = {
      labels: ['A', 'B', 'C'],
      datasets: [
          {
              data: [300, 50, 100],
              backgroundColor: [
                  "#FF6384",
                  "#36A2EB",
                  "#FFCE56"
              ],
              hoverBackgroundColor: [
                  "#FF6384",
                  "#36A2EB",
                  "#FFCE56"
              ]
          }]
  };

  this.lightOptions = {
      legend: {
          labels: {
              fontColor: '#495057'
          }
      }
  };
    this.state = {presentRegion:'India',loading:true};
  }


  componentDidMount() {
    this.getData();
  }

  async getData()
  {
     let url = this.state.presentRegion === 'World' ? WorldDataApi : IndiaDataApi

     try{
        //fetch data corresponding to the region selected
        const fetcher = await fetch(url)
        const response = await fetcher.json()
        await this.setState({data:response,loading:false})
     } 
     catch(err){
       console.log(err);
     } 
  }

  showRegionData = async(region) =>{
     try{
        await this.setState({
          presentRegion:region,
          data:[],
          loading:true
        })
        this.getData();
     }
     catch(err)
     {
       console.log(err)
     }
  }

  render() {
    const {loading,presentRegion} = this.state;
    let pageData,loader;

    let regionnavigationStyleindia = "2.6px solid red" ;
    let regionnavigationStyleWorld = "2.6px solid transparent";

    if(this.state.presentRegion === "World"){
      regionnavigationStyleindia ="2.6px solid transparent";
      regionnavigationStyleWorld = "2.6px solid red";
    }

    pageData = <div>
    <div className="header">
      <h5 style={{ padding: '10px' }}>
        <a href="/" className="navbar-brand router-link-exact-active active" target="_self"><img src={require("./viruslogo.png")} alt="Covid" height="30" width="30" className="d-inline-block align-top mr-1" />COVID-19 </a>
        <div style={{display:"flex",float:"right"}}>
          <p val="india"  onClick={()=>this.showRegionData('India')} style={{height:"40px",cursor:"pointer",color:presentRegion==='India'?'red':'black',borderBottom:regionnavigationStyleindia,fontWeight:"500"}}> <i className="fa fa-map-marker"></i>&nbsp;India</p>
          <p val="world" onClick={()=>this.showRegionData('World')} style={{cursor:"pointer",marginLeft:'25px',color:presentRegion==='India'?'black':'red',borderBottom:regionnavigationStyleWorld}}><i className="fa fa-globe"></i>&nbsp;World</p>
        </div>
      </h5>
    </div><br></br><br></br><br></br>
    <WorldPieChart presentRegion={this.state.presentRegion} data={this.state.data}/>
    <div className="container-fluid">
      <div className="row">
        <AffectedCountries data={this.state.data} presentRegion={this.state.presentRegion} />
        <CoronoMap presentRegion={this.state.presentRegion} data={this.state.data}/>
        <Check data={this.state.data} />
        <CountryData  presentRegion={this.state.presentRegion} data={this.state.data} />
      </div>
    </div>
    <br></br><br></br><br></br>
    <div style={{backgroundColor:"light",height:"40px"}}>
      <span style={{textAlign:"center",display:"block",fontWeight:"300"}}>Source: 
MOHFW(India) and John Hopkins </span>
    </div>
        <div className="footer">
          <div style={{textAlign:"center",margin:"0px auto",display:"block",marginTop:"12px"}}>
             {/* <span><i className="fa fa-envelope"></i>&nbsp;&nbsp;matchaavinash22@gmail.com</span><br></br><br></br> */}
             {/* <span>
               <i className="fa fa-facebook"></i>&nbsp;&nbsp;&nbsp;
               <i className="fa fa-instagram"></i>&nbsp;&nbsp;&nbsp;
               <i className="fa fa-twitter"></i>&nbsp;&nbsp;&nbsp;
             </span><br></br> */}
             <span style={{fontSize:"15px"}}>Copyright Â© 2020, Matcha Avinash</span>
          </div>
        </div>
    </div>

    loader = 
    <div className = "fetching">
      <span><img className="rotate" src={require("./viruslogo.png")} width="70"></img></span>
    </div>

    let data = loading?loader:pageData;


    return (
      <div style={{ backgroundColor: "rgb(248,248,248)"}}>
        {data}
      </div>
    );
  }
}

export default App;
