import React from 'react';
import Highcharts from 'highcharts/highcharts';
import drilldown from 'highcharts/modules/drilldown.js'
import HighchartsReact from 'highcharts-react-official';


drilldown(Highcharts);

class ColumnGraph extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
        options: {
        }
       }
    }

    componentDidMount()
    {
        this.structureData("confirmed");
    }

    structureData(val)
    { 
      try{
        let data =this.props.data.total[val];

        let i=0,DaysInMonths =[31,29,31,30,31,30,31,31,30,31,30,31];
        let nameOfMonths = {0:"Jan",1:"Feb",2:"March",3:"Apr",4:"May",5:"Jun",6:"Jul",7:"Aug",8:"Sept",9:"Oct",10:"Nov",11:"Dec"}

        let obj =[],month=0,prevdata=0,ddrObject=[],daysdata=[],daysPrevData=0;


        data.forEach((element,index) => {
            month = new Date(element[0]).toISOString().split("-")[1];
            daysPrevData = 0;

            if(index>0)
                daysPrevData+=data[index-1][1];
        
            daysdata.push([new Date(element[0]).toISOString().split("-")[2].split("T")[0],element[1]-daysPrevData]);

            if(new Date(element[0]).toISOString().split("-")[2].split("T")[0] == DaysInMonths[month-1]){

                ddrObject.push({name:nameOfMonths[month-1],"id":i.toString(),"data":daysdata});
                daysdata=[];
                if(i>0) prevdata+=obj[i-1].y;

                obj[i]={name:nameOfMonths[month-1],"y":element[1]-prevdata,drilldown:i.toString()};

                i++;
            }
            else if(index == data.length-1){
                prevdata+=obj[i-1].y;
                ddrObject.push({name:nameOfMonths[month-1],"id":i.toString(),"data":daysdata});
                daysdata=[];
                obj[i] = {name:nameOfMonths[month-1],"y":element[1]-prevdata,drilldown:i.toString()};
            }      
        });

        this.setState({
            options: {
                chart: {
                    type: 'column',
                    height:350
                },
                title: {
                    text: this.props.presentRegion+' Coronovirus Cases, 2020'
                },
                subtitle: {
                    text: 'Monthly/Daily Cases'
                },
                
                xAxis: {
                    type: 'category',
                },
                yAxis: {
                    title: {
                        text: 'Cases'
                    }
                },
                legend: {
                    enabled: false
                },
                credits:{
                    enabled:false
                },
                plotOptions: {
                    series: {
                        borderWidth: 0,
                        dataLabels: {
                            enabled: true,
                            format: '{point.y}'
                        }
                    }
                },
                accessibility: {
                    announceNewData: {
                        enabled: true
                    }
                },
                tooltip: {
                    enabled:true,
                    formatter: function() {
                        return this.series.name+" <b>"+this.point.name+"</b><br/>" +val+":<b>"+(this.point.y).toLocaleString()+"</b>"
                    },
                     useHTML:true
                },
            
                series: [
                    {
                       name:"Month", 
                        data:obj,
                    }
                ],
                drilldown: {
                    name:"Dates",
                    series: ddrObject
                }
            }
        });
       }
       catch(err)
       {
           console.log(err);
       }
    }

    render()
    {
        return(
            <div className="card">
                <div className="card-header">
                    <span onClick={()=>this.structureData("confirmed")}>Confirmed</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span onClick={()=>this.structureData("recovered")}>Recovered</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span onClick={()=>this.structureData("deaths")}>Deaths</span>
                </div>
                <hr></hr>
                <div className="card-body">
                 
                  <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                </div>
                {/* <div class="row m-0 row-group text-center border-top border-light-3">
		   <div class="col-12 col-lg-4">
		     <div class="p-3">
		       <h5 class="mb-0">45.87M</h5>
			   <small class="mb-0">Overall Visitor <span> <i class="fa fa-arrow-up"></i> 2.43%</span></small>
		     </div>
		   </div>
		   <div class="col-12 col-lg-4">
		     <div class="p-3">
		       <h5 class="mb-0">15:48</h5>
			   <small class="mb-0" >Visitor Duration <span> <i class="fa fa-arrow-up"></i> 12.65%</span></small>
		     </div>
		   </div>
		   <div class="col-12 col-lg-4">
		     <div class="p-3">
		       <h5 class="mb-0">245.65</h5>
			   <small class="mb-0" >Pages/Visit <span> <i class="fa fa-arrow-up"></i> 5.62%</span></small>
		     </div>
		   </div>
		 </div> */}
            </div>
        )
    }
}

export default ColumnGraph;
