import React from 'react';
import {ProgressBar} from 'primereact/progressbar';

class AffectedCountries extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
           data:[],
           name:'',
        }
    }

    componentDidMount()
    {
      this.sortData();       
    }

    sortData()
    {
      try{

        //sort mapped data according the cases confirmed
        this.props.data.affectedcountries.sort((a,b) => {return b.cases-a.cases})

        // get the top five affected places according to the confirmed cases
        this.props.data.affectedcountries = this.props.data.affectedcountries.splice(0,5);
    
        this.setState({data: this.props.data.affectedcountries,name:this.props.presentRegion === "World" ? "Countries" :"States"});
      }
      catch(err)
      {
        console.log(err);
      } 
    }

    render()
    {
        return(
           <div className="col-xl-3 col-md-12">
              <div className="card" style={{height:'360px'}}>
                <div className="card-header">
                  <span style={{fontSize:"18px"}}>5 Most Affected {this.state.name}</span>
                </div>
                <div className="card-body">
                  { this.state.data.map(data => ( 
                     <div key={data.name}>
                       <span  style={{float:'left'}}>{data.name}</span>  <span style={{float:"right"}}>{data.cases.toLocaleString()}</span><br></br>
                       <ProgressBar value={data.percentage} showValue ={false} style={{height:'5px'}} />
                       <br></br>
                     </div>   ))}
                </div>
              </div>
            </div>
        );
    }
}

export default AffectedCountries;
