import React from 'react';
import {ProgressBar} from 'primereact/progressbar';

const nf = new Intl.NumberFormat();

class AffectedCountries extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
           data:[],
           name:'Countries',
           url:props.url
        }
    }

    componentWillReceiveProps(props)
    {
      if(props.refresh)this.getData(props.presentRegion);
    }

    componentDidMount()
    {
       //SHOW THE WORLD DATA 
       this.getData(this.props.presentRegion);
       
    }

    async getData(region)
    {
      let url ="";

      if(region === 'World')
        url = "https://coronavirus-19-api.herokuapp.com/countries";
      else
        url = "https://covid-india-cases.herokuapp.com/states/";

      await fetch(url)
        .then(response => response.json())
        .then(data => {this.sortData(data,region);  
      });
      
    }

    sortData(data,region)
    {
        var totalCases =0; var obj=[],name="";
        if(region === 'World'){
           name="Countries";
           totalCases = data.splice(0,1);
           obj=data.splice(0,5);
           obj.forEach(element => {
             element["percentage"] = (element.cases/totalCases[0].cases)*100;
             element["place"] = element.country;
           });
        }
        else{
          let ar =[];
           data.forEach(x=>{
             ar.push(parseInt(x.noOfCases));
             totalCases+=x.noOfCases;
              // obj.push({"place": x.state,"cases":x.noOfCases})
           });
           ar.sort(function(a, b){return b - a});
           
           ar.forEach(x=>{
             data.forEach(elem =>{
               if(elem.noOfCases == x) obj.push({place: elem.state,cases:x,percentage:(x/totalCases)*100});
             })
           })
           obj = obj.splice(0,5);
           name="States";
        }
  
        this.setState({data: obj,name:name});

    }

    render()
    {
        return(
           <div className="col-xl-3 col-md-4">
              <div className="card" style={{height:'360px'}}>
        <div className="card-header">5 Most Affected {this.state.name}</div>
                <div className="card-body">
                  { this.state.data.map(name => ( 
                     <div key={name.country}>
                       <span  style={{float:'left'}}>{name.place}</span>  <span style={{float:"right"}}>{name.cases.toLocaleString()}</span><br></br>
                       <ProgressBar value={name.percentage} showValue ={false} style={{height:'5px'}} />
                       <br></br>
                     </div>   ))}
                </div>
              </div>
            </div>
        );
    }
}

export default AffectedCountries;
