import React from 'react';

import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {InputText} from 'primereact/inputtext'


    



class tableData extends React.Component
{

    constructor(props)
    {
        super();
        console.log(props);
        this.state={data:[],districtData:[],name:props.presentRegion == "World" ? "Countries" :"States"};
    }

    componentDidMount()
    {
        this.getData();
    }

    getData(){
        
      let obj = [];
      this.props.data.countries.forEach(element => {
  
      let confirmed = this.props.data.data[element].confirmed;
      let deaths = this.props.data.data[element].deaths;
      let recovered = this.props.data.data[element].recovered;

      obj.push({"name":element,
      "cases":confirmed[confirmed.length-1][1],
      "deaths":deaths[deaths.length-1][1],
      "recovered":recovered[recovered.length-1][1],
      "changeinConfirmed":confirmed[confirmed.length-1][1]-confirmed[confirmed.length-2][1],
      "changeinRecovered" : recovered[recovered.length-1][1]-recovered[recovered.length-2][1],
      "changeinDeaths":deaths[deaths.length-1][1]-deaths[deaths.length-2][1],
      "active":confirmed[confirmed.length-1][1]-deaths[deaths.length-1][1]-recovered[recovered.length-1][1]})  

      });    

     this.setState({data:obj});
    }

    getDistrictData = async(data) =>{

        if(this.props.presentRegion === "World") return;    
        let obj =[];
        const fetcher= await fetch("https://api.covid19india.org/state_district_wise.json")
        const response =await fetcher.json()
        Object.keys(response[data].districtData).forEach(x=>{    
        obj.push({name:x,active:response[data].districtData[x].active,deaths:response[data].districtData[x].deceased,recovered:response[data].districtData[x].recovered,cases:response[data].districtData[x].confirmed})    
        })

        await this.setState({districtData:obj,presentState:data+" - District Wise Status"})

        // scroll to the specified district table
        var element = document.querySelector("#district");
        element.scrollIntoView();
    }

    render = () =>{
        let column,districtTable;
            
        const header = <div>
         {this.state.name}" Wise Status"
        <div  className="col-sm-12 col-xl-3" className="p-datatable-globalfilter-container">
            <InputText width="300px" type="search" placeholder="Search By Place Name" />
        </div>
    </div>
        
        
        if(this.props.presentRegion === 'India' && this.state.districtData.length>0){
         districtTable = <DataTable id="district" rowHover emptyMessage="No records found" responsive={true} style={{border:'none'}} header={this.state.presentState} value={this.state.districtData}  paginator={true} rows={10}>
         <Column field="name"  body={this.dateTemplate=(rowData)=>{return <a onClick={() => this.getDistrictData(rowData.name)}>{rowData.name}</a>}} header="District" sortable={true} filter={true} />
         <Column field="cases"  header="Confirmed" sortable={true} filter={true} />
         <Column field="active" header="Active" sortable={true} filter={true} />
         <Column field="recovered" header="Recovered" sortable={true} filter={true} />
         <Column field="deaths" header="Deaths" sortable={true} filter={true} />
         </DataTable>
        }
        return(
            <div className="col-sm-12 datatable">
              <DataTable onRowClick ={(event)=>this.getDistrictData(event.data.name)} className="p-datatable-places" rowHover emptyMessage="No records found" header={header} value={this.state.data} responsive={true} paginator={true} rows={10}>
                 <Column field="name"   header="Country" sortable={true} body={(rowData)=>{ return <span style={{fontWeight:"550"}}>{rowData.name}</span>}} />     
                 <Column field="cases" body={(rowData)=>{
                      let changes;
                      if(rowData.changeinConfirmed !=0) changes = <span title="Change since yesterday" className="fa fa-arrow-up" style={{fontWeight:'500',color:"#ef3c3c"}}>{rowData.changeinConfirmed}</span>
                      return <span>{rowData.cases} &nbsp;&nbsp;&nbsp;{changes}</span>}} header="Confirmed" sortable={true}  />
                 <Column field="active" header="Active" sortable={true}  />
                 <Column field="recovered" header="Recovered"  body ={(rowData)=>{
                     let changes;
                     if(rowData.changeinRecovered != 0) changes = <span title="Change since yesterday" className="fa fa-arrow-up" style={{fontWeight:'500',color:"#009933"}}>{rowData.changeinRecovered}</span> 
                     return <span>{rowData.recovered}&nbsp;&nbsp;&nbsp;{changes}</span>}} sortable={true}  />
                 <Column field="deaths" body ={(rowData)=>{
                     let changes;
                     if(rowData.changeinDeaths != 0)  changes = <span title="Change since yesterday" className="fa fa-arrow-up" style={{fontWeight:'500',color:"#ef3c3c"}}>{rowData.changeinDeaths}</span>
                 return <span>{rowData.deaths}&nbsp;&nbsp;&nbsp;{changes}</span>}} header="Deaths" sortable={true}  />
              </DataTable>
              <br></br>
              {districtTable}
            </div>
        )
    }
}

export default tableData;
