import React from 'react';
// import TopPanel from './components/TopPanel';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import WorldPieChart from './components/WorldPieChart';
import CoronoMap from './components/CoronaMap';
import CountryData from './components/tableData';
import AffectedCountries from './components/AffectedCountries';


const API = "https://coronavirus-19-api.herokuapp.com/countries";

const RegionApi = 'https://extreme-ip-lookup.com/json/';


class App extends React.Component {

  constructor() {
    super();
    this.state = {presentRegion:'World', refreshed:false,region: 'World' , API:API+'/World','refreshed':false};
  }


  region='';

  componentDidMount() {

    // navigator.geolocation.getCurrentPosition(
    //   function(position) {
    //     console.log(position,'position');
    //   },
    //   function(error) {
    //     console.error("Error Code = " + error.code + " - " + error.message);
    //   }
    // );


    fetch(RegionApi)
      .then(res => res.json())
      .then(response => {
        // response.country = response.country == 'United States' ? response.country='USA': response.country
                this.region=response.country;
        this.setState({API:API+'/World',presentRegion:'World',region:response.country,refreshed:false});
      })
      .catch((data, status) => {
        console.log('Request failed',data);
      })
  }

  showSelectedCountryData = () =>{
    this.setState({region:this.region,presentRegion:this.region,refreshed:true,'API': API+'/'+this.region});
  }

  showGlobalData = () =>{
    this.setState({presentRegion:'World',region:this.region,refreshed:true,'API':API+'/World'})
  }

  render() {

    const {region} = this.state;

    return (
      <div style={{ backgroundColor: "rgb(248,248,248)" }}>
        <div className="header">
          <h5 style={{ padding: '10px' }}>
            <a href="/" className="navbar-brand router-link-exact-active active" target="_self"><img src="https://covid19site.netlify.app/img/virus.3d9b68c5.png" alt="Covid" height="30" width="30" className="d-inline-block align-top mr-1" />COVID-19 </a>
            <div style={{ float: 'right' }}>
              <span className="locations" onClick={this.showGlobalData}><i className="fa fa-globe" style={{ color: '#4f96ae' }}></i>&nbsp;World</span>
              <span style={{ marginLeft: '25px' }} className="locations" onClick={this.showSelectedCountryData}><i className="fa fa-map-marker" style={{ color: 'red' }}></i>&nbsp;{region}</span>
            </div>
          </h5>
        </div>
        <WorldPieChart url={this.state.API} refresh={this.state.refreshed} presentRegion={this.state.presentRegion} region={this.state.region}/>
        <div className="container-fluid">
          <div className="row" >
            <AffectedCountries refresh={this.state.refreshed} presentRegion={this.state.presentRegion} />
            <CoronoMap />
          </div>
        </div>
        <CountryData url={API} /><br></br><br></br>
      </div>
    );
  }
}

export default App;
