import React from 'react';
import Highcharts from 'highcharts/highcharts';
import HighchartsReact from 'highcharts-react-official';


class ColumnGraph extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
        options: {
        //     chart: {
        //         type: 'column',
        //         height:320,
        //     },
        //     title: {
        //         text: 'Monthly Cases Year (2020)'
        //     },
        //     subtitle: {
        //         text: 'Coronovirus Cases    '
        //     },
        //     xAxis: {
        //         categories: [
        //             'Jan',
        //             'Feb',
        //             'Mar',
        //             'Apr',
        //             'May',
        //             'Jun',
        //             'Jul',
        //             'Aug',
        //             'Sep',
        //             'Oct',
        //             'Nov',
        //             'Dec'
        //         ],
        //     },
        //     credits:{
        //         enabled:false
        //     },
        //     yAxis: {
        //         min: 100,
        //         title: {
        //             text: 'Cases'
        //         }
        //     },
        //     series: [{
        //         name:'Cases',
        //         data: [1200,5009,19000,30000,39900, 47800, 79333]
        
        //     }]
        // }};
        }
    }
     //this.getData(props.presentRegion)
    }

    componentDidMount()
    {
        this.structureData(this.props.data);
    }

    structureData(data)
    { 
        let i=0,DaysInMonths =[31,29,31,30,31,30,31,31,30,31,30,31];

        let obj =[],month=1,prevdata=0,ddrObject=[],daysdata=[];

        
        month = data[0].Date.split("-")[1];

        data.forEach((element,index) =>{

            if(index>0) prevdata+=obj[i-1].y;
            daysdata.push([element.Date.split("-")[2].split("T")[0],element.Cases-prevdata])
            obj[i]={"y":element.Cases-prevdata,"drilldown":i};

            if(month != element.Date.split("-")[1])
            {
                ddrObject.push({"id":i,data:daysdata})
                i++;
                daysdata=[];
            }
 
        });

        console.log("fffffff",obj,ddrObject);

        this.setState({
            options: {
                chart: {
                    type: 'column',
                    height:320,
                },
                title: {
                    text: 'Monthly Cases Year (2020)'
                },
                subtitle: {
                    text: 'Coronovirus Cases    '
                },
                plotOptions: {
                    series: {
                        borderWidth: 0,
                        dataLabels: {
                            enabled: true,
                            format: '{point.y}'
                        }
                    }
                },
                xAxis: {
                    categories: [
                        'Jan',
                        'Feb',
                        'Mar',
                        'Apr',
                        'May',
                        'Jun',
                        'Jul',
                        'Aug',
                        'Sep',
                        'Oct',
                        'Nov',
                        'Dec'
                    ],
                },
                credits:{
                    enabled:false
                },
                yAxis: {
                    min: 0,
                    title: {
                        text: 'Cases'
                    }
                },
                series: [{
                    name:'Cases',
                    data: obj
            
                }],
                drilldown:{
                    series:[]
                }
            }
        })
    }

    render()
    {
        return(
            <div className="card">
                <div className="card-body">
                  <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                </div>
            </div>
        )
    }
}

export default ColumnGraph;
