import React from 'react';
import Highcharts from 'highcharts/highcharts';
import HighchartsReact from 'highcharts-react-official';


class ColumnGraph extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
        options: {
            chart: {
                type: 'column',
                height:320,
            },
            title: {
                text: 'Monthly Cases Year (2020)'
            },
            subtitle: {
                text: 'Coronovirus Cases    '
            },
            xAxis: {
                categories: [
                    'Jan',
                    'Feb',
                    'Mar',
                    'Apr',
                    'May',
                    'Jun',
                    'Jul',
                    'Aug',
                    'Sep',
                    'Oct',
                    'Nov',
                    'Dec'
                ],
            },
            credits:{
                enabled:false
            },
            yAxis: {
                min: 100,
                title: {
                    text: 'Cases'
                }
            },
            series: [{
                name:'Cases',
                data: [1200,5009,19000,30000,39900, 47800, 79333]
        
            }]
        }
     };
     //this.getData(props.presentRegion)
    }

    componentWillReceiveProps(props){
     
      this.getData(props.presentRegion);

    }

    getData(region)
    {
        if(region !== 'World')
        var url = "https://api.covid19api.com/dayone/country/"+region+"/status/confirmed";
        fetch(url)
        .then(response => response.json())
        .then(data => {      
        //   this.pieData= data;  
            this.structureData(data); 
        });
    }

    structureData(data)
    { 
        let i =0 ,obj=[],month=null;
        console.log(data);
        data.forEach(element => {
            if(element.Date.split("-")[1] != month) i++;
            obj[i] += element.Cases;
            month=element.Date.split("-")[1];
        });

        this.setState({
            options: {
                chart: {
                    type: 'column',
                    height:320,
                },
                title: {
                    text: 'Monthly Cases Year (2020)'
                },
                subtitle: {
                    text: 'Coronovirus Cases    '
                },
                xAxis: {
                    categories: [
                        'Jan',
                        'Feb',
                        'Mar',
                        'Apr',
                        'May',
                        'Jun',
                        'Jul',
                        'Aug',
                        'Sep',
                        'Oct',
                        'Nov',
                        'Dec'
                    ],
                },
                credits:{
                    enabled:false
                },
                yAxis: {
                    min: 100,
                    title: {
                        text: 'Cases'
                    }
                },
                series: [{
                    name:'Cases',
                    data: obj
            
                }]
            }
        })
    }

    render()
    {
        return(
            <div className="card">
                <div className="card-body">
                  <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                </div>
            </div>
        )
    }
}

export default ColumnGraph;
