import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import highchartsMap from "highcharts/modules/map";
import proj4 from "proj4";
import world from "@highcharts/map-collection/custom/world.geo.json";
import india from "@highcharts/map-collection/countries/in/custom/in-all-disputed.geo.json";
import code from "../country-codes-lat-long-alpha3.json";

highchartsMap(Highcharts);


  if (typeof window !== "undefined") {
    window.proj4 = window.proj4 || proj4;
  }


  

class CoronoMap extends React.Component{

  constructor(props)
  {
    super();
    console.log(code,"codes");
    let mapdata;
    mapdata = props.presentRegion ==="World" ? world:india
    this.state =({
      mapOptions:{
        chart: {
          map: 'countries/ie/ie-all',
          height:320
        },
        title: {
          text:'India Map Plot With Cases'
        },
        credits: {
            enabled: false
        },
        mapNavigation: {
          enabled: true
        },
      tooltip: {
          headerFormat: '',
          pointFormat: '<b>{point.keyword}</b><br>Active:{point.z}</b>'
        },
        series: [{
          // Use the gb-all map with no data as a basemap
          name: 'Basemap',
          mapData: mapdata,
          borderColor: '#A0A0A0',
          nullColor: 'rgba(200, 200, 200, 0.3)',
          showInLegend: false
        }, {
          // Specify points using lat/lon
          type: 'mapbubble',
          name: 'Cities',
          color: '#4169E1',
          data: [{ z: 10729, keyword: "Delhi", lat: 20.59, lon:78.9}, 
                 { z: 16367, keyword: "Assam", lat: 26.2006, lon: 92.9376 }],
          cursor: 'pointer',
          point: {
            events: {
              click: function() {
                console.log(this.keyword);
              }
            }
          }
        }]
      }

    })
  }

    render(){
        return (
          <div className="col-xl-9 col-md-8">
              <div className="card" style={{marginBottom:'30px'}}>
                 <div className="card-body">
                    <HighchartsReact
   constructorType ={'mapChart'}
   highcharts={Highcharts}
   options={this.state.mapOptions}
/>
                 </div>
              </div>
          </div>
        );
    }
}


export default CoronoMap;
