import React from 'react';

import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';


class tableData extends React.Component
{

    constructor(props)
    {
        super();
        this.state={data:[]};
    }


    componentDidMount()
    {
        this.getData();
    }

    getData(){
    let data=this.props.data
    if(this.props.presentRegion === 'World'){
        data=data.Countries
        data.forEach(x=>x["active"] = x.TotalConfirmed-(x.TotalDeaths+x.TotalRecovered))
    }
     else{     
        data.forEach(element => {
            element["Country"] = element.state;
            element["TotalConfirmed"] = element.noOfCases;
            element["TotalDeaths"] = element.deaths;
            element["TotalRecovered"] = element.cured;
            element["active"] = element.noOfCases-element.deaths-element.cured;  
        });
     }

     this.setState({data:data});
    }

    render(){
        return(
            <div className="col-sm-12">
              <DataTable style={{border:'none'}} value={this.state.data}  paginator={true} rows={10}>
                 <Column field="Country" header="Country" sortable={true} filter={true} />
                 <Column field="TotalConfirmed" header="Confirmed" sortable={true} filter={true} />
                 <Column field="active" header="Active" sortable={true} filter={true} />
                 <Column field="TotalRecovered" header="Recovered" sortable={true} filter={true} />
                 <Column field="TotalDeaths" header="Deaths" sortable={true} filter={true} />
              </DataTable>
            </div>
        )
    }
}

export default tableData;
