import React from 'react';
import Highcharts from 'highcharts/highcharts';
import drilldown from 'highcharts/modules/drilldown.js'
import HighchartsReact from 'highcharts-react-official';

drilldown(Highcharts);

class ColumnGraph extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
        options: {
        //     chart: {
        //         type: 'column',
        //         height:320,
        //     },
        //     title: {
        //         text: 'Monthly Cases Year (2020)'
        //     },
        //     subtitle: {
        //         text: 'Coronovirus Cases    '
        //     },
        //     xAxis: {
        //         categories: [
        //             'Jan',
        //             'Feb',
        //             'Mar',
        //             'Apr',
        //             'May',
        //             'Jun',
        //             'Jul',
        //             'Aug',
        //             'Sep',
        //             'Oct',
        //             'Nov',
        //             'Dec'
        //         ],
        //     },
        //     credits:{
        //         enabled:false
        //     },
        //     yAxis: {
        //         min: 100,
        //         title: {
        //             text: 'Cases'
        //         }
        //     },
        //     series: [{
        //         name:'Cases',
        //         data: [1200,5009,19000,30000,39900, 47800, 79333]
        
        //     }]
        // }};
        }
    }
     //this.getData(props.presentRegion)
    }

    componentDidMount()
    {
        this.structureData(this.props.data);
    }

    structureData(data)
    { 
        let i=0,DaysInMonths =[31,29,31,30,31,30,31,31,30,31,30,31];

        let obj =[],month=0,prevdata=0,ddrObject=[],daysdata=[];


        data.forEach((element,index) => {
            month = element.Date.split("-")[1];
 
            daysdata.push([element.Date.split("-")[2].split("T")[0],element.Cases]);

            if(element.Date.split("-")[2].split("T")[0] == DaysInMonths[month-1]){

                ddrObject.push({"id":i.toString(),"data":daysdata});
                daysdata=[];
                if(i>0) prevdata+=obj[i-1].y;

                obj[i]={"y":element.Cases-prevdata,drilldown:i.toString()};

                i++;
            }
            else if(index == data.length-1){
                prevdata+=obj[i-1].y;

                obj[i] = {"y":element.Cases-prevdata,drilldown:i.toString()};
            }      
        });

        console.log("fffffff",obj,ddrObject);

        this.setState({
            options: {
                chart: {
                    type: 'column',
                    height:320
                },
                title: {
                    text: 'Coronovirus Cases , 2018'
                },
                // subtitle: {
                //     text: 'Click the columns to view versions. Source: <a href="http://statcounter.com" target="_blank">statcounter.com</a>'
                // },
                
                xAxis: {
                    type: ["Jan"]
                },
                yAxis: {
                    title: {
                        text: 'Cases'
                    }
            
                },
                legend: {
                    enabled: false
                },
                credits:{
                    enabled:false
                },
                plotOptions: {
                    series: {
                        borderWidth: 0,
                        dataLabels: {
                            enabled: true,
                            format: '{point.y}'
                        }
                    }
                },
            
                // tooltip: {
                //     headerFormat: '<span style="font-size:11px">{series.name}</span><br>',
                //     pointFormat: '<span style="color:{point.color}">{point.name}</span>: <b>{point.y:.2f}%</b> of total<br/>'
                // },
            
                series: [
                    {
                        
                        data:obj
                   }
                ],
                drilldown: {
                    series: ddrObject
                }
            }
        });
    }

    render()
    {
        return(
            <div className="card">
                <div className="card-body">
                  <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                </div>
            </div>
        )
    }
}

export default ColumnGraph;
