import React from 'react';
import {ProgressBar} from 'primereact/progressbar';


class AffectedCountries extends React.Component
{
    constructor(props)
    {
        super();
        this.state={
           data:[],
           name:'',
        }
        
    }

    componentDidMount()
    {
      this.sortData();       
    }

    sortData()
    {

      let TotalConfirmed = this.props.data.total.confirmed[this.props.data.total.confirmed.length-1][1];

      //MAP CONFIRMED CASES WITH COUNTRY NAME
      let obj = [];
      this.props.data.countries.forEach(element => {

        let countryTotal = this.props.data.data[element].confirmed;
        obj.push({"name":element,"cases":countryTotal[countryTotal.length-1][1],"percentage":(countryTotal[countryTotal.length-1][1]/TotalConfirmed)*100})  

      });

      //sort mapped data according the cases confirmed
      obj.sort((a,b) => {return b.cases-a.cases})

      // get the top five affected places according to the confirmed cases
      obj = obj.splice(0,5);
  
      this.setState({data: obj,name:this.props.presentRegion === "World" ? "Countries" :"States"}); 
    }

    render()
    {
        return(
           <div className="col-xl-3 col-md-4">
              <div className="card" style={{height:'360px'}}>
                <div className="card-header">5 Most Affected {this.state.name}</div>
                <div className="card-body">
                  { this.state.data.map(data => ( 
                     <div key={data.place}>
                       <span  style={{float:'left'}}>{data.name}</span>  <span style={{float:"right"}}>{data.cases.toLocaleString()}</span><br></br>
                       <ProgressBar value={data.percentage} showValue ={false} style={{height:'5px'}} />
                       <br></br>
                     </div>   ))}
                </div>
              </div>
            </div>
        );
    }
}

export default AffectedCountries;
