import React from 'react';
// import TopPanel from './components/TopPanel';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import WorldPieChart from './components/WorldPieChart';
import CoronoMap from './components/CoronaMap';
import CountryData from './components/tableData';
import AffectedCountries from './components/AffectedCountries';
import {BallBeat} from 'react-pure-loaders';


class App extends React.Component {

  constructor() {
    super();
    this.state = {presentRegion:'India',loading:true};
  }


  componentDidMount() {
    this.getData();
  }

  async getData()
  {
     let url = this.state.presentRegion === 'World' ? "https://nprelease.indiatimes.com/ncov19/trend_data_toi.json" : "https://nprssfeeds.indiatimes.com/ncov19/trend_data_india_toi.json";

     try{
        //fetch data corresponding to the region selected
        const fetcher = await fetch(url)
        const response = await fetcher.json()
        await this.setState({data:response,loading:false})
     } 
     catch(err){
       console.log(err);
     } 
  }

   showIndiaData = async() => {
    await this.setState({
      presentRegion:'India',
      data:[],
      loading:true
    });
    this.getData();
  }

   showGlobalData  = async() =>{ 
    await this.setState({
      presentRegion:'World',
      data:[],
      loading:true
    });
    this.getData();
  }

  render() {
    const {region,loading} = this.state;
    let pageData,loader;
    if(!loading){
      pageData = <div>
    <div className="header">
      <h5 style={{ padding: '10px' }}>
        <a href="/" className="navbar-brand router-link-exact-active active" target="_self"><img src="https://covid19site.netlify.app/img/virus.3d9b68c5.png" alt="Covid" height="30" width="30" className="d-inline-block align-top mr-1" />COVID-19 </a>
        {/* <div style={{ float: 'right' }}>
          <span className="locations" onClick={this.showGlobalData}><i className="fa fa-globe" style={{ color: '#4f96ae' }}></i>&nbsp;World</span>
          <span style={{ marginLeft: '25px' }} className="locations" onClick={this.showSelectedCountryData}><i className="fa fa-map-marker" style={{ color: 'red' }}></i>&nbsp;India</span>
        </div> */}
        <div style={{display:"flex",float:"right"}}>
          <p val="india" onClick={this.showIndiaData} style={{borderBottomColor:"red",color:"red",borderBottom:"2px solid transparent",fontWeight:"500"}}> <i className="fa fa-map-marker"></i>&nbsp;India</p>
          <p val="world" onClick={this.showGlobalData} style={{marginLeft:'25px'}}><i className="fa fa-globe" style={{ color: '#4f96ae' }}></i>&nbsp;World</p>
        </div>
      </h5>
    </div>
    <WorldPieChart presentRegion={this.state.presentRegion} data={this.state.data}/>
    <div className="container-fluid">
      <div className="row" >
        <AffectedCountries data={this.state.data} presentRegion={this.state.presentRegion} />
        <CoronoMap presentRegion={this.state.presentRegion}/>
      </div>
    </div>
    <CountryData  presentRegion={this.state.presentRegion} data={this.state.data} /><br></br><br></br>
    </div>
  }
  else{
    loader = 
    <div className = "fetching">
      <BallBeat 
        color={'#123abc'}
        loading={this.state.loading}
      />
    </div>
  }

    return (
      <div style={{ backgroundColor: "rgb(248,248,248)" }}>
        {loader}
       {pageData}
      </div>
    );
  }
}

export default App;
