import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import highchartsMap from "highcharts/modules/map";
import proj4 from "proj4";
import world from "@highcharts/map-collection/custom/world.geo.json";
import india from "@highcharts/map-collection/countries/in/custom/in-all-disputed.geo.json";
import code from "./../countrycordinates.json";

highchartsMap(Highcharts);


  // if (typeof window !== "undefined") {
  //   window.proj4 = window.proj4 || proj4;
  // }


  

class CoronoMap extends React.Component{

  constructor(props)
  {
    super();
    this.state={mapOptions:{},mapdata:props.presentRegion === "World" ? world:india};
  }

  componentDidMount(){
    this.structureData();
    this.setState({
      mapOptions:{
        chart: {
          map: 'countries/ie/ie-all',
          height:320,
        },
        title: {
          text:this.props.presentRegion+' Map Plot With Cases <span class="fa fa-expand"></span>'
        },
        credits: {
            enabled: false
        },
        mapNavigation: {
          enabled: true,
          buttonOptions: {
            verticalAlign: 'bottom'
          }
        },
      tooltip: {
          headerFormat: '',
          pointFormat: '<b>{point.country}</b><br>Active:{point.z}</b>'
        },
        series: [{
          // Use the gb-all map with no data as a basemap
          name: 'Basemap',
          data: code.ref_country_codes,
          mapData: this.state.mapdata,
          joinBy: ["iso-a3", "alpha3"],
          borderColor: '#A0A0A0',
          nullColor: 'rgba(200, 200, 200, 0.3)',
          showInLegend: false,
          states: {
            hover: {
              color: "red"
            }
          }
        }, 
        ]
      }
    })
  }

  structureData()
  {
    this.props.data.countries.forEach(country=>{
      code.ref_country_codes.forEach(x=>{
         if(x.country === country) x["z"] = this.props.data.data[country].confirmed[this.props.data.data[country].confirmed.length-1][1];
      })
    })
  }

    render(){
        return (
          <div className="col-xl-9 col-md-8">
              <div className="card" style={{marginBottom:'30px'}}>
                 <div className="card-body">
                    <HighchartsReact id="expand" constructorType ={'mapChart'} highcharts={Highcharts} options={this.state.mapOptions} />
                 </div>
              </div>
          </div>
        );
    }
}


export default CoronoMap;
