import React from 'react';
import Highcharts from 'highcharts/highcharts';
import HighchartsReact from 'highcharts-react-official';


class ColumnGraph extends React.Component
{
    constructor(props)
    {
        super();
        console.log(props.data,"const");
        this.state={
        options: {
            chart: {
                type: 'column',
                height:320,
            },
            title: {
                text: 'Monthly Cases Year (2020)'
            },
            subtitle: {
                text: 'Coronovirus Cases    '
            },
            xAxis: {
                categories: [
                    'Jan',
                    'Feb',
                    'Mar',
                    'Apr',
                    'May',
                    'Jun',
                    'Jul',
                    'Aug',
                    'Sep',
                    'Oct',
                    'Nov',
                    'Dec'
                ],
            },
            credits:{
                enabled:false
            },
            yAxis: {
                min: 100,
                title: {
                    text: 'Cases'
                }
            },
            series: [{
                name:'Cases',
                data: [1200,5009,19000,30000,39900, 47800, 79333]
        
            }]
        }
     };
     //this.getData(props.presentRegion)
    }

    componentWillReceiveProps(props){

        if(props.refresh){
           this.structureData(props.data);
        }
    }

    componentDidMount()
    {
        this.structureData(this.props.data);
    }

    structureData(data)
    { 
        console.log(data,"column");
        let i =0 ,obj=[],month=1;
        data.forEach((element,index) => {

            if(element.Date.split("-")[1] != month || (data.length-1 == index)){
                 let prevdata=0;
                if(i>0){
                    obj.forEach(x=> prevdata += x);
                }

                if(data.length - 1 == index){ obj[i]=data[index].Cases-prevdata; return;}
                obj[i] = data[index-1].Cases-prevdata;
                i++;
            } 
            month=element.Date.split("-")[1];
           
        });

        this.setState({
            options: {
                chart: {
                    type: 'column',
                    height:320,
                },
                title: {
                    text: 'Monthly Cases Year (2020)'
                },
                subtitle: {
                    text: 'Coronovirus Cases    '
                },
                xAxis: {
                    categories: [
                        'Jan',
                        'Feb',
                        'Mar',
                        'Apr',
                        'May',
                        'Jun',
                        'Jul',
                        'Aug',
                        'Sep',
                        'Oct',
                        'Nov',
                        'Dec'
                    ],
                },
                credits:{
                    enabled:false
                },
                yAxis: {
                    min: 0,
                    title: {
                        text: 'Cases'
                    }
                },
                series: [{
                    name:'Cases',
                    data: obj
            
                }]
            }
        })
    }

    render()
    {
        return(
            <div className="card">
                <div className="card-body">
                  <HighchartsReact highcharts={Highcharts} options={this.state.options} />
                </div>
            </div>
        )
    }
}

export default ColumnGraph;
