import React from 'react';

import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';


class tableData extends React.Component
{

    constructor(props)
    {
        super();
        this.state={data:[]};
    }


    componentDidMount()
    {
        this.getData(this.props.data,this.props.presentRegion);
    }

    getData(data,region){
    if(region === 'World')
        data.splice(0,1);
        //this.setState({data:data});
     else{     
        data.forEach(element => {
            element["country"] = element.state;
            element["cases"] = element.noOfCases;
            element["deaths"] = element.deaths;
            element["recovered"] = element.cured;
            element["active"] = element.noOfCases-element.deaths-element.cured;  
        });
        //this.setState({data:data});  
     }

     this.setState({data:data});
    }

    //  fetch('https://extreme-ip-lookup.com/json/')
    //  .then( res => res.json())
    //  .then(response => {
    //   console.log("Country: ", response.country);
    //  })
    //  .catch((data, status) => {
    //    console.log('Request failed');
    //  })

    

    render(){
        return(
            <div className="col-sm-12">
              <DataTable value={this.state.data}  paginator={true} rows={10}>
                 <Column field="country" header="Country" sortable={true} filter={true} />
                 <Column field="cases" header="Confirmed" sortable={true} filter={true} />
                 <Column field="active" header="Active" sortable={true} filter={true} />
                 <Column field="recovered" header="Recovered" sortable={true} filter={true} />
                 <Column field="deaths" header="Deaths" sortable={true} filter={true} />
              </DataTable>
            </div>
        )
    }
}

export default tableData;
