import  React  from "react";
import {ProgressBar} from 'primereact/progressbar';


class Rates extends React.Component{
    constructor()
    {
        super();
        this.state={active:{},recovered:{},deaths:{}};
    }

    componentDidMount(){
        this.structureData();
    }

   async structureData(){
         console.log(this.props.data,"cons");
         let active = {},recovered={},deaths={};
         let totalConfirmed = this.props.data.total.recovered[this.props.data.total.recovered.length-1];
         let totalRecovered = this.props.data.total.recovered[this.props.data.total.recovered.length-1];
         let totalDeaths = this.props.data.total.deaths[this.props.data.total.deaths.length-1];

         let totalActive = totalConfirmed-totalRecovered-totalDeaths;
        //find the highest active cases rate

        Object.keys(this.props.data.data).forEach((x,index)=>{
                if(index==0){
                    active[x]=this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1];
                    recovered[x]=this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1];
                    deaths[x]=this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1];
                }
                else{
                if(parseInt(Object.values(active))< this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1]){
                    active={};
                    active[x]=this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1];
                }

                if(parseInt(Object.values(recovered))< this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]){
                    recovered={};
                   recovered[x]=((this.props.data.data[x].recovered[this.props.data.data[x].recovered.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100).toFixed(2);
                }

                if(parseInt(Object.values(deaths))< this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1]){
                    deaths={};
                    deaths[x]=((this.props.data.data[x].deaths[this.props.data.data[x].deaths.length-1][1]/this.props.data.data[x].confirmed[this.props.data.data[x].confirmed.length-1][1])*100).toFixed(2);
                }
                }
        })  
    console.log("",recovered,deaths,active);
    await this.setState({active:active,recovered:recovered,deaths:deaths});
    console.log(this.state.active);


    }


    render(){
        return(
            <div className="col-xl-3 col-md-12">
              <div className="card p-disabled" style={{height:"360px"}}>
                <div className="card-body">
                    <span style={{float:"left"}}>Highest Active Rate</span>
                    <hr style={{border:"2px solid rgb(124, 181, 236)"}}></hr>
                    <span style={{float:"left"}}>{Object.keys(this.state.active).toString()}</span>
                    <span style={{float:"right",color:"#ef3c3c"}}>{this.state.active[Object.keys(this.state.active).toString()]}%</span>
                    <br></br><br></br><br></br>
                    <span style={{float:"left"}}>Highest Recovery Rate</span>
                    <hr style={{border:"2px solid #009933"}}></hr>
                    <span style={{float:"left"}}>{Object.keys(this.state.recovered).toString()}</span>
                    <span style={{float:"right",color:"#009933"}}>{this.state.recovered[Object.keys(this.state.recovered).toString()]}%</span>
                    <br></br><br></br><br></br>
                    <span style={{float:"left"}}>Highest Death Rate</span>
                    <hr style={{border:"2px solid red"}}></hr>
                    <span style={{float:"left"}}>{Object.keys(this.state.deaths).toString()}</span>
                    <span style={{float:"right",color:"red"}}>{this.state.deaths[Object.keys(this.state.deaths).toString()]}%</span>

                </div>
              </div>
            </div>
        )
    }
}

export default Rates;
